const request = require('supertest');

// The app must be exported from app.js as module.exports = app
const app = require('./src/app');

describe('GET /hello', () => {
  test('returns 200 and the exact hello message', async () => {
    const res = await request(app).get('/hello');

    expect(res.statusCode).toBe(200);
    expect(res.headers['content-type']).toMatch(/application\/json/);
    expect(res.body).toEqual({ message: 'Hello, world!' });
  });

  test('returns 400 if a request body is provided', async () => {
    const res = await request(app)
      .get('/hello')
      .send({ anything: 'not allowed' });

    expect(res.statusCode).toBe(400);
    expect(res.headers['content-type']).toMatch(/application\/json/);
    expect(res.body).toEqual({ error: 'Request body must be empty' });
  });
});
