describe('GET /items', () => {

  describe('Positive cases (200 OK)', () => {

    test('Returns 200 with default page (1) and pageSize (10) when no parameters are provided', async () => {
      const res = await request(app).get('/items');

      expect(res.statusCode).toEqual(200);
      expect(Array.isArray(res.body.items)).toBe(true);
      expect(typeof res.body.totalItems).toBe('number');
      expect(typeof res.body.currentPage).toBe('number');
      expect(typeof res.body.totalPages).toBe('number');

      expect(res.body.currentPage).toEqual(1);
      expect(res.body.items.length).toBeLessThanOrEqual(10);
      if (res.body.items.length > 0) {
        res.body.items.forEach(item => {
          expect(typeof item.id).toBe('string');
          expect(item.id.length).toBeGreaterThan(0);
          expect(typeof item.name).toBe('string');
          expect(item.name.length).toBeGreaterThan(0);
          expect(item.name.length).toBeLessThanOrEqual(100);
        });
        const ids = res.body.items.map(item => item.id);
        expect(new Set(ids).size).toEqual(ids.length);
      }
    });

    test("Returns 200 with specified valid positive integer 'page' and 'pageSize' within the 1-50 range", async () => {
      const res = await request(app).get('/items?page=2&pageSize=5');

      expect(res.statusCode).toEqual(200);
      expect(Array.isArray(res.body.items)).toBe(true);
      expect(typeof res.body.totalItems).toBe('number');
      expect(typeof res.body.currentPage).toBe('number');
      expect(typeof res.body.totalPages).toBe('number');

      expect(res.body.currentPage).toEqual(2);
      expect(res.body.items.length).toBeLessThanOrEqual(5);
    });

    test("Returns 200 when 'pageSize' is set to its minimum valid value (1)", async () => {
      const res = await request(app).get('/items?pageSize=1');

      expect(res.statusCode).toEqual(200);
      expect(Array.isArray(res.body.items)).toBe(true);
      expect(typeof res.body.totalItems).toBe('number');
      expect(typeof res.body.currentPage).toBe('number');
      expect(typeof res.body.totalPages).toBe('number');

      expect(res.body.items.length).toBeLessThanOrEqual(1);
    });

    test("Returns 200 when 'pageSize' is set to its maximum valid value (50)", async () => {
      const res = await request(app).get('/items?pageSize=50');

      expect(res.statusCode).toEqual(200);
      expect(Array.isArray(res.body.items)).toBe(true);
      expect(typeof res.body.totalItems).toBe('number');
      expect(typeof res.body.currentPage).toBe('number');
      expect(typeof res.body.totalPages).toBe('number');

      expect(res.body.items.length).toBeLessThanOrEqual(50);
    });

    test("Returns 200 with an empty 'items' array and correct pagination metadata if no items exist in the system", async () => {
      const res = await request(app).get('/items?totalItems=0'); // Assuming a mock to simulate no items

      expect(res.statusCode).toEqual(200);
      expect(res.body.items).toEqual([]);
      expect(res.body.totalItems).toEqual(0);
      expect(res.body.currentPage).toEqual(1);
      expect(res.body.totalPages).toEqual(0);
    });

    test("Returns 200 with an empty 'items' array and correct pagination metadata if the requested 'page' is beyond the total available pages", async () => {
      const res = await request(app).get('/items?page=999&pageSize=10');

      expect(res.statusCode).toEqual(200);
      expect(res.body.items).toEqual([]);
      expect(typeof res.body.totalItems).toBe('number');
      expect(res.body.totalItems).toBeGreaterThanOrEqual(0);
      expect(res.body.currentPage).toEqual(999);
      expect(typeof res.body.totalPages).toBe('number');
      expect(res.body.totalPages).toBeLessThan(999);
    });

  });

  describe('Negative cases (400 Bad Request)', () => {

    test("Returns 400 when 'page' parameter is provided as a non-integer value (string)", async () => {
      const res = await request(app).get('/items?page=abc');
      expect(res.statusCode).toEqual(400);
    });

    test("Returns 400 when 'page' parameter is provided as a non-integer value (float)", async () => {
      const res = await request(app).get('/items?page=1.5');
      expect(res.statusCode).toEqual(400);
    });

    test("Returns 400 when 'page' parameter is 0 (non-positive)", async () => {
      const res = await request(app).get('/items?page=0');
      expect(res.statusCode).toEqual(400);
    });

    test("Returns 400 when 'page' parameter is a negative integer", async () => {
      const res = await request(app).get('/items?page=-1');
      expect(res.statusCode).toEqual(400);
    });

    test("Returns 400 when 'pageSize' parameter is provided as a non-integer value (string)", async () => {
      const res = await request(app).get('/items?pageSize=xyz');
      expect(res.statusCode).toEqual(400);
    });

    test("Returns 400 when 'pageSize' parameter is provided as a non-integer value (float)", async () => {
      const res = await request(app).get('/items?pageSize=10.5');
      expect(res.statusCode).toEqual(400);
    });

    test("Returns 400 when 'pageSize' parameter is 0 (out-of-range)", async () => {
      const res = await request(app).get('/items?pageSize=0');
      expect(res.statusCode).toEqual(400);
    });

    test("Returns 400 when 'pageSize' parameter is a negative integer (out-of-range)", async () => {
      const res = await request(app).get('/items?pageSize=-5');
      expect(res.statusCode).toEqual(400);
    });

    test("Returns 400 when 'pageSize' parameter is greater than 50 (out-of-range)", async () => {
      const res = await request(app).get('/items?pageSize=51');
      expect(res.statusCode).toEqual(400);
    });

  });

});
