const express = require("express");

const app = express();
app.use(express.json());

const users = [{ id: 1, email: "a@test.com" }];

app.get("/users/:id", (req, res) => {
  const id = Number(req.params.id);
  const user = users.find(u => u.id === id);

  if (!user) {
    return res.status(404).end();
  }

  res.json(user);
});

module.exports = app;

