const express = require("express");

const app = express();
app.use(express.json());

let todos = [];

app.post("/todos", (req, res) => {
  const { title } = req.body;

  if (!title) {
    return res.status(400).end();
  }

  const todo = { id: todos.length + 1, title };
  todos.push(todo);

  res.status(201).json(todo);
});

module.exports = app;

